import 'package:flutter/material.dart';import 'dart:developer';void main() => runApp(MyApp());class MyApp extends StatelessWidget {  @override  Widget build(BuildContext context) {    return MaterialApp(      title: "Calculadora de Victor Rosario",      theme: ThemeData(        primarySwatch: Colors.red,      ),      home: HomePage(),    );  }}class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int num1 = 0;  int num2 = 0;  String texttodisplay = "00";  String res = "";  String operation = "";  void btnclicked(String btntext) {    //debugger();    try {      if (btntext == "C") {        num1 = 0;        num2 = 0;        texttodisplay = "";        res = "";      } else if (btntext == "+" || btntext == "-" || btntext == "x" ||          btntext == "/") {        num1 = int.parse(texttodisplay);        res = "";        operation = btntext;      } else if (btntext == "=") {        num2 = int.parse(texttodisplay);        switch (operation) {          case "+":            res = (num1 + num2).toString();            break;          case "-":            res = (num1 - num2).toString();            break;          case "x":            res = (num1 * num2).toString();            break;          case "/":            res = (num1 ~/ num2).toString();            break;          default:            res = int.parse(texttodisplay + btntext).toString();            break;        }      } else {        res = int.parse(texttodisplay + btntext).toString();      }    }    catch(IntegerDivisionByZeroException){      debugPrint(IntegerDivisionByZeroException);    }    setState(() {      texttodisplay = res;    });  }  Widget custimButton(String btnval) {    return Expanded(      child: OutlineButton(        padding: EdgeInsets.all(20.0),        onPressed: () => btnclicked(btnval),        child: Text(          "$btnval",          style: TextStyle(            fontSize: 20.0,          ),        ),      ),    );  }  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        title: Text("Calculadora"),      ),      body: Container(        child: Column(          mainAxisAlignment: MainAxisAlignment.end,          children: <Widget>[            Expanded(              child: Container(                padding: EdgeInsets.all(10.0),                alignment: Alignment.bottomRight,                child: Text(                  "$texttodisplay",                  style: TextStyle(fontSize: 20.0, fontWeight: FontWeight.w600),                ),              ),            ),            Row(              children: <Widget>[                custimButton("9"),                custimButton("8"),                custimButton("7"),                custimButton("+"),              ],            ),            Row(              children: <Widget>[                custimButton("6"),                custimButton("5"),                custimButton("4"),                custimButton("-"),              ],            ),            Row(              children: <Widget>[                custimButton("3"),                custimButton("2"),                custimButton("1"),                custimButton("x"),              ],            ),            Row(              children: <Widget>[                custimButton("C"),                custimButton("0"),                custimButton("="),                custimButton("/"),              ],            ),          ],        ),      ),    );  }}